import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { updateSlot } from "./actions";
import { useForm } from "react-hook-form";
import "./index.css";

const About = (props) => {
  const { id } = props.match.params;
  const history = useHistory();
  const dispatch = useDispatch();
  const slots = useSelector((state) => state.timeSlots);
  const [selectedSlot, setSelectedSlot] = useState({});
  const { handleSubmit, register } = useForm({
    mode: "onSubmit",
    defaultValues: selectedSlot,
  });

  useEffect(() => {
    let timeSlot = slots.find((slot) => slot.id === +id);
    setSelectedSlot(timeSlot);
  }, [slots, id]);

  const saveDetails = (data) => {
    console.log("hiii", data);
    let x = { ...selectedSlot, ...data };
    debugger;
    dispatch(updateSlot({ ...selectedSlot, ...data }));
    // history.push("/");
  };

  const onCancel = () => {
    history.push("/");
  };

  return (
    <div className="about-container">
      <div className="about-card">
        {/* {selectedSlot ? (
          <h1> {selectedSlot?.startTime}</h1>
        ) : (
          <h1>Not found</h1>
        )} */}
        <form
          onSubmit={handleSubmit((data) => saveDetails(data))}
          className="form"
        >
          <div className="container">
            <section>
              <h2>Booking Details</h2>
            </section>
            <section>
              <label>First Name:</label>
              <input name="firstName" className="input" ref={register} />
            </section>
            <section>
              <label>Last Name:</label>
              <input name="lastName" className="input" ref={register} />
            </section>
            <section>
              <label>Phone Number:</label>
              <input name="phone" className="input" ref={register} />
            </section>
            <div className="button-container">
              <button
                type="button"
                className="cancel-button"
                onClick={onCancel}
              >
                CANCEL
              </button>
              <button type="submit" className="save-button">
                SAVE
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default About;
