import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { updateSlot } from "./actions";
import { useForm } from "react-hook-form";
import "./index.css";

const About = (props) => {
  const { id } = props.match.params;
  const history = useHistory();
  const dispatch = useDispatch();
  const slots = useSelector((state) => state.timeSlots);
  const [selectedSlot, setSelectedSlot] = useState({});
  const { register, handleSubmit, watch, errors } = useForm();

  const defaultValues = selectedSlot;

  useEffect(() => {
    let timeSlot = slots.find((slot) => slot.id === +id);
    setSelectedSlot(timeSlot);
  }, [slots, id]);

  const saveDetails = () => {
    dispatch(updateSlot(selectedSlot));
    history.push("/");
  };

  const onCancel = () => {
    history.push("/");
  };

  const onSubmit = (data) => console.log(data);
  return (
    <div className="about-container">
      <div className="about-card">
        {/* {selectedSlot ? (
          <h1> {selectedSlot?.startTime}</h1>
        ) : (
          <h1>Not found</h1>
        )} */}
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="container">
            <section>
              <label>First Name:</label>
              <input name="firstName" className="input" ref={register} />
            </section>
            <section>
              <label>Last Name:</label>
              <input name="lastName" className="input" ref={register} />
            </section>
            <section>
              <label>Phone Number:</label>
              <input name="phone" className="input" ref={register} />
            </section>
          </div>
          <button type="button" onClick={onCancel}>
            CANCEL
          </button>
          <button type="submit" onClick={saveDetails}>
            SAVE
          </button>
        </form>
      </div>
    </div>
  );
};

export default About;
