import { SELECTED_SLOT } from "../constants";

const initialState = {
  count: 0,
  isIncrementing: false,
  isDecrementing: false,
};

const aboutReducer = (state = initialState, action) => {
  switch (action.type) {
    case SELECTED_SLOT:
      return {
        ...state,
        isIncrementing: true,
      };

    default:
      return state;
  }
};
export default aboutReducer;
