import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { updateSlot } from "./actions";

const About = (props) => {
  const { id } = props.match.params;
  const history = useHistory();
  const dispatch = useDispatch();
  const slots = useSelector((state) => state.timeSlots);
  const [selectedSlot, setSelectedSlot] = useState({});

  useEffect(() => {
    let timeSlot = slots.find((slot) => slot.id === +id);
    setSelectedSlot(timeSlot);
  }, [slots, id]);

  const saveDetails = () => {
    dispatch(updateSlot(selectedSlot));
    history.push("/");
  };

  return (
    <>
      {selectedSlot ? <h1> {selectedSlot?.startTime}</h1> : <h1>Not found</h1>}
      <button onClick={saveDetails}>SAVE</button>
    </>
  );
};

export default About;
